%% Implementacion particular del metodo de Newton%clear all; close all; clc;f1 = @(x,y) cos(x)-y+2*x; f2 = @(x,y) y-x.^2;f = @(x,y) [f1(x,y);            f2(x,y)];%Derivadas parciales de f1 y f2df1_dx = @(x,y) -sin(x)+2; % derivada de f1 respecto a xdf1_dy = @(x,y) -1; % derivada de f1 respecto a ydf2_dx = @(x,y) -2.*x; % derivada de f2 respecto a xdf2_dy = @(x,y) 1; % derivada de f2 respecto y Df = @(x,y) [df1_dx(x,y) df1_dy(x,y);             df2_dx(x,y) df2_dy(x,y)];x0 = [4;15];epsilon = 10^(-8);% Tolerancia max_it = 1000; % Numero maximo de iteracionesfor it=1:max_it   x1 = x0-inv(Df(x0(1),x0(2)))*f(x0(1),x0(2));    if abs(x1-x0) < epsilon     break   else     x0 = x1;   end    endsprintf('La solucion del sistema es x = %d e y = %d',x1(1),x1(2))